import React, { Component } from 'react'
/*
    register component
 */
import {
    NavBar,
    WingBlank,
    List,
    InputItem,
    WhiteSpace,
    Radio,
    Button
} from 'antd-mobile'
import Logo from '../../components/logo/logo'

export default class Register extends Component {
    state = {
        username: '',
        password: '',
        password2: '',
        type: 'expert'
    }

    // deal with input field / check box changes, collect data to state
    handleChange = (name, value) => {
        this.setState({ [name]: value })
    }
    // skip to login router
    toLogin = () => {
        this.props.history.replace('/login')
    }
    // register
    register = () => {
        console.log(JSON.stringify(this.state))
    }

    render() {
        const { type } = this.state
        return (
            <div>
                <NavBar>Bob Recruitment</NavBar>
                <Logo />
                <WingBlank>
                    <List>
                        <InputItem
                            placeholder='输入用户名'
                            onChange={val => this.handleChange('username', val)}
                        >
                            用户名:
                        </InputItem>
                        <WhiteSpace />
                        <InputItem
                            type='password'
                            placeholder='输入密码'
                            onChange={val => this.handleChange('password', val)}
                        >
                            密&nbsp;&nbsp;&nbsp;码:
                        </InputItem>
                        <WhiteSpace />
                        <InputItem
                            type='password'
                            placeholder='输入确认密码'
                            onChange={val => this.handleChange('password2', val)}
                        >
                            确认密码:
                        </InputItem>
                        <WhiteSpace />
                        <List.Item>
                            <span style={{ marginRight: 30 }}>用户类型:</span>
                            <Radio checked={this.state.type === 'dashen'}
                                onClick={() => { this.handleChange('type', 'dashen') }}>大神</Radio>
                            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                            <Radio checked={this.state.type === 'laoban'}
                                onClick={() => { this.handleChange('type', 'laoban') }}>老板</Radio>
                        </List.Item>
                        <WhiteSpace />
                        <Button type='primary' onClick={this.register}>注&nbsp;&nbsp;&nbsp;册
                        </Button>
                        <WhiteSpace />
                        <Button onClick={this.toLogin}>已经有账号</Button>
                    </List>
                </WingBlank>
            </div>
        )
    }
}
