/**
 * There are lots of reducer functions: 
 *      They will return a new state according to old state and specified action
 */
import { combineReducers } from 'redux'
import { AUTH_SUCCESS, ERROR_MSG } from './action-types'

const initUser = {
    username: '', // username
    type: '',     // type
    msg: '',      // error info
    redirectTo: '', // need auto redirect router path
}
/**
 * 
 */
function user1(state = initUser, action) {
    switch (action.type) {
        case AUTH_SUCCESS: // data is user
            return { ...action.data, redirectTo: '/' };
        case ERROR_MSG:    // data is msg
            return { ...state, msg: action.data };
        default:
            return state;
    }
}

export default combineReducers({
    user1
})
/**
 * to export state's structure: {user1:{}}
 */

/**
 * router:
 * /boss
 * /expert
 * /bossInfo
 * /expertInfo
 *
 * check if info done? user.header hold value or not
 * check the type of user? user.type
 */

function getRedirectTo(type, header) {
    let path = '';
    if (type === 'boss') {
        path = '/boss'
    } else {
        path = '/expert'
    }
    if (header) {
        path += 'Info'
    }
    return path;
}
