/**
 * Boss Info Router Container
 */
import React, { Component } from 'react'
import { connect } from 'react-redux'
import { NavBar, InputItem, TextareaItem, Button } from 'antd-mobile'
import HeaderSelector from '../../components/header-selector/header-selector'

class BossInfo extends Component {
    state = { // same with database data name
        header: '',
        position: '',
        company: '',
        salary: '',
        info: ''
    }

    handleChange = (name, val) => {
        this.setState({
            [name]: val
        })
    }

    render() {
        return (
            <div>
                <NavBar>Complete Boss Info</NavBar>
                <HeaderSelector />
                <InputItem placeholder="Please enter hiring position" onChange={val => { this.handleChange('position', val) }}>Position:</InputItem>
                <InputItem placeholder="Please enter company name">Company:</InputItem>
                <InputItem placeholder="Please enter salary">Salary:</InputItem>
                <TextareaItem placeholder="Please enter your requirement" title="Asking" rows={3} />
                <Button type='primary'>Save</Button>
            </div>
        )
    }
}

export default connect(
    state => (
        {}
    ),
    {}
)(BossInfo)
