import React, { Component } from 'react'
import { connect } from 'react-redux'
import { List, Badge } from 'antd-mobile'
const Item = List.Item
const Brief = Item.Brief

/**
 *  group chatMsgs by chat_id, get last item
 * 1. find out last msg for each chat, and store it by obj container {chat_id: lastMsgs}
 * 2. get all array of lastMsgs
 * 3. sort the array by (create_time desc)
 */
function getLastMsgs(chatMsgs) {
    let lastMsgObjs = {};
    chatMsgs.forEach(msg => {
        const chatId = msg.chat_id;
        let lastMsg = lastMsgObjs[chatId];
        if (!lastMsg) {
            lastMsgObjs[chatId] = msg;
        } else {
            if (msg.create_time > lastMsg.create_time) {
                lastMsgObjs[chatId] = msg;
            }
        }
    })

    return lastMsgObjs;
}

class Message extends Component {
    render() {
        const { user } = this.props;
        const { users, chatMsgs } = this.props.chat;
        const lastMsgs = getLastMsgs(chatMsgs);


        return (
            <List style={{ marginTop: 50, marginBottom: 40 }}>
                <Item
                    extra={<Badge text={3} />}
                    thumb={require(`../../assets/img/avatar1.png`).default}
                    arrow='horizontal'
                >
                    你好
                    <Brief>nr1</Brief>
                </Item>
                <Item
                    extra={<Badge text={0} />}
                    thumb={require(`../../assets/img/avatar2.png`).default}
                    arrow='horizontal'
                >
                    你好2
                    <Brief>nr2</Brief>
                </Item>
            </List>
        )
    }
}
export default connect(
    state => ({ user: state.user1, chat: state.chat1 }),
    {}
)(Message)
