import React, { Component } from 'react'
import { List, Grid } from 'antd-mobile'
import PropTyes from 'prop-types' // pass value to parent comp
/**
 * Header Selector Component
 */
export default class HeaderSelector extends Component {

    static propTypes = {
        setHeader: PropTyes.func.isRequired
    }

    state = {
        icon: null
    }

    constructor(props) {
        super(props);
        this.headerList = [];
        for (let i = 0; i < 20; i++) {
            this.headerList.push({
                text: 'Avatar ' + (i + 1),
                icon: require(`./img/avatar${i + 1}.png`).default // not use import
            })
        }
    }

    render() {
        const listHeader = 'Please select your avatar'
        return (
            <List renderHeader={() => listHeader}>
                <Grid data={this.headerList} columnNum={5} />
            </List>
        )
    }
}
