import React, { Component } from 'react'
/**
 * login component
 */
import {
    NavBar,
    WingBlank,
    List,
    InputItem,
    WhiteSpace,
    Button
} from 'antd-mobile'
import Logo from '../../components/logo/logo'

export default class Login extends Component {
    state = {
        username: '',
        password: '',
    }
    // deal with input field / check box changes, collect data to state
    handleChange = (name, value) => {
        this.setState({ [name]: value })
    }
    // skip to register router
    toRegister = () => {
        this.props.history.replace('/register')
    }
    // login
    login = () => {
        console.log(this.state)
    }

    render() {
        return (
            <div>
                <NavBar>Bob Recruitment</NavBar>
                <Logo />
                <WingBlank>
                    <List>
                        <InputItem
                            placeholder='Please input username'
                            onChange={val => this.handleChange('username', val)}
                        >
                            Username:
                        </InputItem>
                        <WhiteSpace />
                        <InputItem
                            type='password'
                            placeholder='Please input password'
                            onChange={val => this.handleChange('password', val)}
                        >
                            Password:
                        </InputItem>
                        <WhiteSpace />
                        <Button type='primary' onClick={this.login}>Login
                        </Button>
                        <WhiteSpace />
                        <Button onClick={()=> this.toRegister}>Don't have an account?</Button>
                    </List>
                </WingBlank>
            </div>
        )
    }
}
